CREATE TABLE t_m_buku (
    ID INT PRIMARY KEY,
    Edisi VARCHAR(255),
    Pengarang VARCHAR(255),
    Deskripsi TEXT,
    Penerbit VARCHAR(255),
    Judul VARCHAR(255),
    Jumlah_Buku INT,
    Bahasa VARCHAR(50),
    Gambar VARCHAR(255),
    Subjek VARCHAR(255),
    Topik VARCHAR(255),
    Id_Buku_Master INT
);



SELECT
    ID,
    Judul,
    Pengarang AS penulis,
    Penerbit,
    Deskripsi AS abstrak,
    Edisi,
    Jumlah_Buku AS banyak_buku,
    Bahasa,
    2000 AS tahun_terbit, -- Default karena tidak ada di tabel lama
    NULL AS isbn, -- Default NULL
    500 AS jumlah_halaman, -- Default karena tidak ada di tabel lama
    TRUE AS status
FROM t_m_buku;

drop table t_m_buku;